@app.route('/get_country', methods=['GET'])
def get_country():
    code = request.args.get('code')
    
    if code in data:
        # If code is passed, return a single response with the code as the parent key
        return jsonify({code: data[code]})
    else:
        # If code is not passed, return the full list
        return jsonify(data)

